import React, { useMemo, useState } from "react";
import "./AppointmentForm.css";

/**
 * AppointmentForm
 * Props:
 *  - isOpen: boolean
 *  - doctor: { id, name, specialty, experienceYears, rating }
 *  - onClose: () => void
 *  - onSubmit: (payload) => void
 */
export default function AppointmentForm({ isOpen, doctor, onClose, onSubmit }) {
  const [name, setName] = useState("");
  const [phone, setPhone] = useState("");
  const [appointmentDate, setAppointmentDate] = useState("");
  const [appointmentTime, setAppointmentTime] = useState("");
  const [timeSlot, setTimeSlot] = useState("");
  const [futureDate, setFutureDate] = useState("");

  if (!isOpen) return null;

  const today = new Date().toISOString().slice(0, 10);

  const slots = useMemo(
    () => [
      "09:00 – 09:30",
      "09:30 – 10:00",
      "10:00 – 10:30",
      "14:00 – 14:30",
      "14:30 – 15:00",
      "15:00 – 15:30",
    ],
    []
  );

  const validate = () => {
    if (!name.trim()) return "Please enter patient name.";
    if (!phone.trim()) return "Please enter phone number.";
    if (!(appointmentDate || futureDate)) return "Pick a date (today) or a future date.";
    if (!(appointmentTime || timeSlot)) return "Pick a time or a time slot.";
    return null;
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    const err = validate();
    if (err) return alert(err);

    const payload = {
      id: crypto.randomUUID(),
      doctorId: doctor?.id,
      doctorName: doctor?.name,
      name: name.trim(),
      phone: phone.trim(),
      date: futureDate || appointmentDate,
      time: appointmentTime || null,
      timeSlot: timeSlot || null,
      createdAt: Date.now(),
    };

    onSubmit?.(payload);
    onClose?.();

    // reset
    setName("");
    setPhone("");
    setAppointmentDate("");
    setAppointmentTime("");
    setTimeSlot("");
    setFutureDate("");
  };

  return (
    <div className="booking-form bg-white rounded-xl shadow p-5">
      <h3 className="text-lg font-semibold mb-2">
        {doctor?.name} {doctor?.specialty ? `• ${doctor.specialty}` : ""}
      </h3>

      <form onSubmit={handleSubmit} className="space-y-3">
        <div className="form-row">
          <label className="form-label">Name:</label>
          <input
            className="form-input"
            placeholder="Patient name"
            value={name}
            onChange={(e) => setName(e.target.value)}
            required
          />
        </div>

        <div className="form-row">
          <label className="form-label">Phone Number:</label>
          <input
            className="form-input"
            placeholder="(xx) xxxxx-xxxx"
            inputMode="tel"
            value={phone}
            onChange={(e) => setPhone(e.target.value)}
            required
          />
        </div>

        {/* ✅ Date of Appointment */}
        <div className="form-row">
          <label className="form-label">Date of Appointment:</label>
          <input
            type="date"
            className="form-input"
            value={appointmentDate}
            onChange={(e) => setAppointmentDate(e.target.value)}
            min={today}
          />
        </div>

        {/* Optional free time input */}
        <div className="form-row">
          <label className="form-label">Appointment Time (HH:MM):</label>
          <input
            type="time"
            className="form-input"
            value={appointmentTime}
            onChange={(e) => setAppointmentTime(e.target.value)}
          />
        </div>

        {/* ✅ Book Time Slot */}
        <div className="form-row">
          <label className="form-label">Book Time Slot:</label>
          <select
            className="form-input"
            value={timeSlot}
            onChange={(e) => setTimeSlot(e.target.value)}
          >
            <option value="">Select a time slot</option>
            {slots.map((s) => (
              <option key={s} value={s}>
                {s}
              </option>
            ))}
          </select>
        </div>

        {/* ✅ Future date (book for coming dates) */}
        <div className="form-row">
          <label className="form-label">Book for a future date:</label>
          <input
            type="date"
            className="form-input"
            value={futureDate}
            onChange={(e) => setFutureDate(e.target.value)}
            min={today}
          />
          <small className="text-muted">
            Optional — overrides “Date of Appointment” if set.
          </small>
        </div>

        <div className="flex gap-2 justify-end pt-2">
          <button type="button" className="btn btn-ghost" onClick={onClose}>
            Close
          </button>
          <button type="submit" className="btn btn-primary">
            Book Now
          </button>
        </div>
      </form>
    </div>
  );
}
