import React, { useMemo, useState } from "react";
import "./FindDoctorSearch.css";

const SPECIALTY_EMOJI = {
  "Dentist": "🦷",
  "Gynecologist/obstetrician": "👶",
  "General Physician": "🩺",
  "Dermatologist": "🧴",
  "Ear-nose-throat (ent) Specialist": "👂",
  "Homeopath": "🌿",
  "Ayurveda": "🪔",
  "Cardiologist": "❤️",
  "Orthopedic": "🦴",
  "Neurologist": "🧠",
};

export default function FindDoctorSearch({ items = [] }) {
  const [q, setQ] = useState("");

  // If the list is provided elsewhere, we keep it; otherwise fallback:
  const data = items.length ? items : useMemo(
    () => [
      "Dentist",
      "Gynecologist/obstetrician",
      "General Physician",
      "Dermatologist",
      "Ear-nose-throat (ent) Specialist",
      "Homeopath",
      "Ayurveda",
    ],
    []
  );

  const filtered = useMemo(
    () => data.filter((s) => s.toLowerCase().includes(q.trim().toLowerCase())),
    [q, data]
  );

  return (
    <div className="fds-wrapper">
      {/* Search input with magnifier button */}
      <div className="fds-search">
        <input
          className="fds-input"
          placeholder="Search doctors, clinics, hospitals, etc."
          value={q}
          onChange={(e) => setQ(e.target.value)}
        />
        <button className="fds-search-btn" aria-label="Search">🔎</button>
      </div>

      {/* Specialty list */}
      <div className="fds-list">
        {filtered.map((label) => (
          <div key={label} className="fds-row">
            <div className="fds-left">
              <span className="fds-emoji">{SPECIALTY_EMOJI[label] || "👤"}</span>
              <span className="fds-name">{label}</span>
            </div>
            <span className="fds-tag">SPECIALITY</span>
          </div>
        ))}
      </div>
    </div>
  );
}
